        public static async Task SeedDefaultUserAsync(UserManager<ApplicationUser> userManager, RoleManager<IdentityRole> roleManager)
        {
            var administratorRole = new IdentityRole("Administrator");
            var moderatorRole = new IdentityRole("Moderator");

            if (roleManager.Roles.All(r => r.Name != administratorRole.Name))
            {
                await roleManager.CreateAsync(administratorRole);
                await roleManager.CreateAsync(moderatorRole);
            }

            var administrator = new ApplicationUser { UserName = "admin", Email = "administrator@gmail.com" };
            administrator.Person = new Person()
            {
                FirstName = "Админ",
                LastName = "Админыч",
                Bio = "Администратор сайта.",
                Rating = 42,
                UserName = administrator.UserName,
                Status = "Просто мой статус"
            };

            var moderator = new ApplicationUser { UserName = "moderator", Email = "moderator@gmail.com" };
            moderator.Person = new Person()
            {
                FirstName = "Модератор",
                LastName = "Модератович",
                Bio = "Модератор сайта. Слежу за соблюдением правил.",
                Rating = 22,
                UserName = moderator.UserName,
                Status = "Мой статус"
            };

            if (userManager.Users.All(u => u.UserName != administrator.UserName))
            {
                await userManager.CreateAsync(administrator, "Qwerty123.");
                await userManager.AddToRolesAsync(administrator, new[] { administratorRole.Name });

                await userManager.CreateAsync(moderator, "Qwerty123.");
                await userManager.AddToRolesAsync(moderator, new[] { moderatorRole.Name });
            }
        }

