                    new Label{ Name="Mind map", Description="Конспект представляет из себя ментальную карту" },
                    new Label{ Name="Перевод", Description="Исходная книга была на иностранном языке" },
                };

                await context.Labels.AddRangeAsync(labels);

                // SUMMARIES
                var summaries = new List<Summary>();
                var persons = context.Persons.ToList();
                var lorem = new Bogus.DataSets.Lorem(locale: "ru");

                foreach (var book in books)
                {
                    var n = random.Number(1, 3);
                    for (var i = 0; i < n; i++)
                    {
                        summaries.Add(new Summary
                        {
                            Book = book,
                            Rating = random.Number(0, 442),
                            IsPublic = true,
                            Creator = random.ListItem(persons),
                            Labels = new List<Label>(random.ListItems(labels, random.Number(1, 3))),
                            About = lorem.Sentences(random.Number(1, 3), " "),
                            Chapters = Enumerable.Range(1, random.Number(1, 15)).Select(v =>
                                new Chapter
                                {
                                    Title = $"Глава {v}",
                                    Number = v,
                                    Content = string.Join("\n\n", Enumerable.Range(1, random.Number(1, 10)).Select(v => lorem.Sentences(random.Number(8, 25), " ")))
                                }).ToList(),
                        }); ; ;
                    }
                }

                var morningMagicsummary = new Summary
                {
                    Book = books.First(b => b.Name == "Магия утра. Как первый час дня определяет ваш успех"),
                    Rating = 520,
                    IsPublic = true,
                    Labels = new List<Label>(random.ListItems(labels, random.Number(1, 3))),
                    About = "Максимально короткая и полезная выжимка из нашумевшей книги о самом выжном часе в жизни каждого человека.",
                    Creator = random.ListItem(persons),
                    Chapters = new List<Chapter>()
                                     {
                                         new Chapter{ Number=1, Title = "Вступление", Content = "Автор книги Хэл Элрод считает, что первый час дня - определяет весь оставшийся день и от него зависит насколько продуктивны вы будете. И предлагает практики, чтобы прожить этот час с максимальной пользой."},
                                         new Chapter{ Number=2, Title = "Практика первая: тишина", Content = @"Попробуйте начать свой не с беготни, а со спокойствием, ясностью и безмятежностью. Лучшим помощником в этом может стать медитация. Ученые утверждают, что она снижает уровень стресса, улучшает концентрацию внимания и даже увеличивает некоторые участки мозга (например, префронтальную кору и гиппокамп).

                 Существует очень большое количество техник, главное – выполнять их. Вот какой алгоритм дает Хэл Элрод:

                 - Удобно сядьте в комфортном, но не слишком расслабляющем месте – чтобы не заснуть.
                 - Максимально сконцентрируйтесь на дыхании. Для этого можно использовать «правило квадрата» – сделайте вдох в течение 3-х секунд, задержите дыхание на 3 секунды, сделайте выдох в течение 3-х секунд и вновь задержите дыхание на такое же время. Повторяйте этот цикл на протяжении всего сеанса.
                 - Старайтесь использовать нижнее дыхание – животом. При этом вы должны вдыхать носом, а выдыхать – ртом.
                 - Ваша стратегическая задача – очистить свой разум от всех мыслей. Они все равно будут атаковать вас – просто принимайте это и продолжайте концентрироваться на дыхании.
                 - Начните с 5 минут и постепенно увеличивайте это время."},
                                         new Chapter{ Number=3, Title = "Практика вторая: аффирмации", Content = @"На самом деле это не какая-то эзотерическая модная техника. Аффирмации – это те же внутренние диалоги, которые мы полубессознательно ведем сами с собой. Но обычно они несут довольно пессимистичный и негативный характер. Конечно, это можно и нужно обратить на пользу. Как с помощью аффирмаций перепрограммировать свое мышление на продуктивность и успех?

                  - Определитесь с целями. Причем сделайте это для каждой стороны жизни – будь то финансы, отношения или физическое здоровье. Опишите, какой вы видите свою идеальную жизнь. Мозг должен знать конкретную точку назначения.
                  - Ответьте на вопрос «Почему?». Несколько увесистых аргументов помогут лучше прояснить цели и сделают их более осознанными. Кроме того, они защитят вас от гнетущих и тормозящих сомнений, которые неизбежно будут появляться на вашем пути.
                  - Опишите, каким человеком вам нужно быть для достижения намеченных целей. Саморазвитие – на то и саморазвитие, что вы вкладываете в первую очередь в себя. Изменить жизнь можно только изменив свою личность – и это, пожалуй, наиболее трудная задача. Первый шаг к ее решению – понять, чего вы хотите от самого себя.
                  - Определите действия, которые приведут к цели. Включите в них точное количество и частоту шагов, которые вы предполагаете предпринять. Скажем, «я буду ходить в спортзал 2 раза в неделю и тратить на это по 30 минут». В таком случае ваше продвижение будет максимально зримым и контролируемым, а значит – успешным.
                  - Используйте чужой опыт. Добавляйте в свои аффирмации высказывания других людей, которые наиболее точно резонируют с вашими мыслями. В этом нет ничего зазорного – наоборот, вы как бы получаете силу тех личностей, чьи цитаты проговариваете.
                  - Кратко и емко запишите ответы на вышеприведенные пункты, а затем читайте их вслух каждое утро – ежедневно. Вы можете совмещать это с занятиями спортом или душем – главное делать это эмоционально и искренне."},
                                         new Chapter{Number=4,  Title = "Практика третья: визуализация", Content = @"Уже довольно давно спортсмены используют эту технику в своих тренировках. А в такой прагматичной области как спорт не задерживается ничто пустое и неэффективное – так что смело можете добавлять эту технику в свой арсенал.

                 Хэлл Элрод рекомендует выполнять эту практику сразу после чтения аффирмаций – и делать это в три этапа:

                  - Подготовьтесь – сядьте удобно, но не в слишком вольной позе, закройте глаза и глубоко дышите. Предварительно можете включить приятную, но ненавязчивую музыку.
                  - Вообразите то, чего хотите достигнуть. Просто создавайте в своем мозгу как можно более яркие и глубокие образы той жизни, к которой вы стремитесь. Подключайте все органы чувств, представляйте эмоции, которые вы будете ощущать. Иными словами – постарайтесь пережить свое будущее. Чем живее это получится – тем больше усилий вы потом приложите, чтобы достигнуть задуманного.
                  - Вообразите того, кем вы хотите стать. Представьте, с каким задором вы будете выполнять свою работу, и с каким удовольствием выходить на утреннюю пробежку. Визуализируйте все те действия, в которых вы будете выражаться как личность и то, как будет реагировать ваше окружение – близкие, друзья и коллеги."},
                                         new Chapter{ Number=5, Title = "Практика четвертая: физические упражнения", Content = "Вам может быть трудно заставить себя – но вы никогда не пожалеете после. У меня на канале вы можете найти записи полноценных тренировок. Дерзайте!"},
                                         new Chapter{ Number=6, Title = "Практика пятая: чтение", Content = @"Чего бы вы не хотели достичь – наверняка кто-то уже сделал что-то подобное. Или хотя бы был в похожей ситуации. И очень вероятно, что рассказал об этом. Сегодня нет смысла изобретать велосипед – достаточно прочитать руководство по его изготовлению. Именно в этом сила чтения – вы можете пройти путь куда быстрее, чем его первооткрыватель. Что сэкономит вам массу времени для открытия своего собственного!
                 Начните хотя бы с 10-ти страниц в день – это даст вам около 20-ти 200-страничных книг в год. Впечатляет? Вот еще несколько советов, чтобы сделать чтение более эффективным:

                  - Контролируйте процесс своими целями. Не забывайте спрашивать себя, для чего вы читаете и что хотите получить от книги.
                  - Загибайте страницы, подчеркивайте, делайте пометки на полях, зачитывайте до дыр – в общем, делайте все, чтобы взять от книги максимум!
                  - Перечитывайте лучшие произведения. Мы редко усваиваем и применяем в жизни даже десятую часть того, о чем узнаем. Кроме того, со временем наш образ мыслей меняется – и мы воспринимаем старую информацию по-новому. Все это говорит о необходимости перечитывания – оно позволяет глубже погрузиться в материал и извлечь из него больше пользы."},
                                         new Chapter{ Number=7, Title = "Практика шестая: ведение дневника", Content = @"Дневник помогает документировать события, впечатления, идеи, планы – все то, что так легко выветривается из головы. Это бесплатная форма психотерапии, которая доступна всегда и везде. Необходимость кратко изложить все самое важное заставляет вас хорошо подумать и сконцентрировать пережитый опыт. А его перечитывание позволяет по-новому взглянуть на старые идеи и жизненные уроки, а также проследить свое развитие.

                 Существует масса аспектов жизни, которые вы можете описывать в дневнике. Можно писать о своих целях, мечтах, планах, достижениях, жизненных уроках, идеях. Можно просто описывать события и свои мысли о них."},
                                         new Chapter{ Number=8, Title = "Стратегия пробуждения: 5 этапов борьбы за бодрое и продуктивное утро", Content = @"Зачастую бывает, что самое трудное - это проснуться и просто заставить себя встать с кровати. Это огромная проблема, с которой сталкивался практически каждый человек. Автора книги тоже не минула сия участь – ведь он всю жизнь считал себя «совой». Но чтобы решить эту задачу, он придумал пятиэтапную стратегию пробуждения:

                 1. Мотивируйте себя заранее
                 У каждого в жизни были дни, когда он просыпался с радостью и энтузиазмом. Определенно, это было связано с чем-то волнующим и незаурядным. Бывали и другие дни, когда вечером вы боялись заснуть, думая о завтрашнем дне, а утром – никак не решались встать.
                 Если утром ваша воля подавлена, пытаться мотивировать себя в таком состоянии – провальная идея. Поэтому, займитесь этим с вечера. Подумайте о том, ради чего вы будете завтра вставать и в результате вы проснетесь с теми же жизнеутверждающими мыслями.

                 2. Ставьте будильник как можно дальше от кровати
                 Простой, но очень эффективный трюк. Вы просто не сможете бессознательно протянуть руку и выключить звонок. Теперь вам придется подняться с кровати, чтобы сделать это. Волей-неволей, но вы просыпаетесь.

                 3. Бегите чистить зубы и умываться
                 Очевидно? Как бы не так. Многие, даже проснувшись, начинают лениво листать ленту в соцсетях или просто нежиться в постели. Но такое простое действие как чистка зубов и умывание лица водой (желательно холодной) практически исключают возможность вернуться в кровать. После этого вы почувствуете себя значительно бодрее.

                 4. Выпейте воды
                 Что было бы, если бы в течение 6-8 часов вы не выпили бы ни капли воды? Наверняка вы бы почувствовали обезвоживание. Подобное состояние замедляет метаболизм в организме и вызывает чувство усталости. Но именно это происходит с вами ночью. Чтобы запустить организм – просто пополните его запасы стаканом воды.

                 5. Наденьте спортивный костюм
                 Физические упражнения – один из ключевых моментов успешного и продуктивного утра. Многие люди сразу же идут в душ, но, как считает Хэл Элрод, вы должны заслужить его, предварительно хорошо попотев. Это затрагивает все сферы – интеллектуальную, духовную, физическую, эмоциональную"},
                                     }
                };
                summaries.Add(morningMagicsummary);

                await context.Summaries.AddRangeAsync(summaries);

                await context.SaveChangesAsync();
            }
        }
    }
}
